import logo from './logo.svg';
import './App.css';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import ListEmployeeComponentUseState from './components/ListEmployeeComponentUseState';
import HeaderComponentUseState from './components/HeaderComponentUseState';
import FooterComponentUseState from './components/FooterComponentUseState';
import AddEmployeeComponent from './components/AddEmployeeComponent';

function App() {
  return (
    <div>
      <Router>
          <HeaderComponentUseState/>
            <div className="container">
              <Switch> 
                <Route exact path = "/" component = {ListEmployeeComponentUseState}></Route>
                <Route path = "/employees" component = {ListEmployeeComponentUseState}></Route>
                <Route path = "/add-employee" component = {AddEmployeeComponent}></Route>
                <Route path = "/update-employee/:id" component = {AddEmployeeComponent}></Route>
              </Switch>
           </div>
          <FooterComponentUseState/>
      </Router>
    </div>
  );
}

export default App;
